        angle = angle % 360
        x2, y2 = x + cos(radians(angle)) * size, y - sin(radians(angle)) * size
        pygame.draw.line(display, (0,255,0), (x,y), (x2,y2))
        if size > 5:
                tree(x2, y2, angle-random.randint(25,35), size-10)
                tree(x2, y2, angle, size-10)
                tree(x2, y2, angle+random.randint(25,35), size-10)
